INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (3, 'Кичари', 70, 50, 5, 'https://yaroslavs-imac.local:80/static/recipes/kichari.jpeg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'Могучая аюрведическая штука, с имбиря неплохо штырит, рекомендую. На удивление хорошо сочетаются рис и маш, считаю, стоит того, чтобы попробовать.', 2), (4, 'Чечевичные котлеты', 60, 90, 3, 'https://yaroslavs-imac.local:80/static/recipes/chech_kotl.jpeg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'Котлеты для правильного питания с неправильным вкусом. Если котлеты, то в них должно быть мясо, но тут только здоровая и полезная чечевица, от которой слегка сводит челюсть. Но это же не остановит тебя в стремлении похудеть?', 2);
