INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (6, 'Яичные конвертики с сыром и ветчиной', 20, 150, 5, 'https://img1.russianfood.com/dycontent/images_upl/379/sm_378723.jpg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'Сытное и вкусное блюдо "с изюминкой", которое можно приготовить всего из 3 ингредиентов, - аппетитные, нежные и румяные яичные конвертики с ветчиной и сыром. Отличная альтернатива привычной яичнице для завтрака или перекус на скорую руку. Попробуйте!', 1);
