INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Подготовьте продукты по списку.', 6), (DEFAULT, 2, 'Натрите ветчину и сыр на мелкой терке', 'В отдельной емкости разбейте яйца и слегка взбейте вилкой.', 6), (DEFAULT, 3, 'Жарка', 'Разогрейте сковорду, смажьте растительным маслом, влейте порцию взбитых яиц и равномерно распределите по сковороде. Жарьте примерно 1-2 минуты.', 6), (DEFAULT, 4, 'Переверните яичный блинчик на другую сторону', 'В центр блинчика выложите немного ветчины и натертого сыра.', 6), (DEFAULT, 5, 'Сверните конвертиком и обжарьте еще 20-30 секунд', 'Повторите процесс с оставшейся яичной массой и начинкой.', 6);
