INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Смешайте 2 яйца и 200 мл молока.', 7), (DEFAULT, 2, 'Добавьте 2 ст.л. сахара и ваниль, взбейте до однородности', '', 7), (DEFAULT, 3, 'Добавьте 10 ст. л. муки и разрыхлитель.', 'Тщательно перемешайте. Тесто получится средней густоты.', 7), (DEFAULT, 4, 'Разогрейте сковородку с антипригарным покрытием', 'На сухую сковороду налейте тесто небольшим половником. Накройте крышкой, и жарьте панкейки на среднем огне.', 7), (DEFAULT, 5, 'Жарьте блинчики до румяности с двух сторон', 'Переворачивайте, когда верхняя поверхность панкейков станет пористой, при этом будет еще влажной.', 7);
