INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Колбасу нарезать тонкими ломтиками. Помидор нарезать тонкими дольками. Сыр натереть на тёрке.', 8), (DEFAULT, 2, 'Замешать тесто', 'В миску выложить сметану и майонез, вбить яйцо, посолить и поперчить. Перемешать до однородности. Всыпать всю муку, замесить.', 8), (DEFAULT, 3, 'Выкладываем ингредиенты на сковороду', 'Холодную сковороду (диаметром 20 см) смазать растительным маслом. Выложить тесто, на тесто выложить колбасу, затем помидоры.', 8), (DEFAULT, 4, 'Жарим пиццу', 'Присыпать сверху тёртым сыром. Поставить сковороду на средний огонь, накрыть крышкой и готовить пиццу на сковороде 20 минут.', 8);
