INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'В кастрюле вскипятите молоко. Непрерывно помешивая молоко венчиком, тонкой струйкой всыпьте манную крупу. Варите кашу около 3 минут, до загустения. Каша должна получиться густой.', 9), (DEFAULT, 2, 'Готовимся к жарке', 'Готовую кашу переложите в миску и оставьте остывать до тёплого состояния. Затем вбейте яйцо, всыпьте сахар, ванилин и маленькую щепотку соли. Перемешайте.', 9), (DEFAULT, 3, 'Формируем биточки', 'Смоченными в воде руками сформируйте из манного "теста" круглую толстенькую лепёшку и обваляйте её в муке. Таким образом сформируйте остальные биточки.', 9), (DEFAULT, 4, 'Жарим биточки', 'Обжарьте биточки на небольшом огне по 3 минуты с каждой стороны, до золотистой корочки.', 9);
