INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Включите духовку, пусть разогревается до 190 градусов.', 11), (DEFAULT, 2, 'Нарежьте продукты', 'Очищенный картофель нарежьте кубиками среднего размера, сосиски - кружочками, помидоры - некрупными кубиками.', 11), (DEFAULT, 3, 'Выкладываем на форму.', 'В глубокой миске соедините картофель, сосиски и помидоры. Добавьте майонез, соль, чёрный молотый перец и молотую паприку. Чеснок очистите, мелко нарежьте и тоже отправьте в миску. Перемешайте и распределите по форме.', 11), (DEFAULT, 4, 'Запекаем', 'Поместите форму с заготовкой в предварительно разогретую до 190 градусов духовку на 40 минут, почти до полной готовности картофеля (зависит от сорта и размера кусочков картошки, а также от особенностей вашей духовки).', 11), (DEFAULT, 5, 'Добавляем сыр', 'По истечении времени достаньте форму из духовки. Твёрдый сыр натрите на крупной тёрке и распределите поверх картофеля с сосисками и помидорами. Верните овощи с сосисками и сыром в духовку и запекайте ещё 10 минут.', 11);
INSERT INTO recipe_food_values VALUES (DEFAULT, 50, 150, 450, 400, 11);
