INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Куриное филе нарежьте небольшими кусочками. Обжарьте на растительном масле несколько минут до полуготовности. Лук мелко нарежьте, добавьте к курице и жарьте куриное филе с луком еще 3-4 минуты.', 12), (DEFAULT, 2, 'Добавляем грибы', 'Шампиньоны крупно нарежьте и добавьте в сковороду. После того, как выпарится влага из грибов, посолите, поперчите и готовьте курицу с луком и грибами еще 5-7 минут, до золотистого цвета.', 12), (DEFAULT, 3, 'Заливаем сливками.', 'Залейте мясо и грибы сливками, немного прогрейте всё вместе.', 12), (DEFAULT, 4, 'Добавляем сыр', 'Сыр натрите на крупной терке и выложите сверху. Выключите огонь и накройте сковороду крышкой. Когда через 3-5 минут сыр расплавится, жюльен с грибами и курицей можно подавать к столу.', 12);
