INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка картофеля', 'Картофель очистите, вымойте вместе с луком, обсушите. Натрите картофель на крупной терке, образовавшуюся жидкость нужно отжать.', 13), (DEFAULT, 2, 'Выкладываем в форму', 'Половину картофеля выложите в смазанную маслом форму для запекания. На картофель выложите мясной фарш, посолите и поперчите его по вкусу.', 13), (DEFAULT, 3, 'Выкладываем вторую часть продуктов.', 'Нарежьте зеленый лук, посыпьте им фарш. Натрите сыр на крупной терке. Смешайте сыр со второй половиной натертого картофеля, добавьте яйцо (или два). Перемешайте, чуть поперчите и посолите', 13), (DEFAULT, 4, 'Запекаем', 'Выложите картофель с сыром на слой фарша, разровняйте ложкой. Готовьте запеканку из картофеля и мясного фарша в предварительно разогретой до 180 градусов духовке примерно 1 час.', 13);
