INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (14, 'Куриные котлеты по-французски', 30, 500, 3, 'https://img1.russianfood.com/dycontent/images_upl/117/sm_116992.jpg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'Куриные котлеты по-французски (другие названия - котлеты по-министерски, по-албански) очень нежные и сочные, просто тают во рту.', 3);
