INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Нарезаем продукты', 'Нарезаем лук и куриное филе квадратиками.', 14), (DEFAULT, 2, 'Смешиваем лук и филе', 'Солим, перчим.', 14), (DEFAULT, 3, 'Готовимся к жарке', 'Добавляем сначала аккуратно одно яйцо. (Если будет не хватать по консистенции, то добавим второе.) Добавляем майонез. Высыпаем муку, перемешиваем массу.', 14), (DEFAULT, 4, 'Жарим', 'Столовой ложкой выкладываем котлеты на сковородку. Обжариваем с каждой стороны по 3-4 минуты.', 14);
