INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (15, 'Макароны с крабовыми палочками, сметаной и чесноком', 25, 500, 5, 'https://img1.russianfood.com/dycontent/images_upl/552/sm_551637.jpg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'Ещё один рецепт в копилку простых, но вкусных блюд. Макароны с крабовыми палочками, сметаной и чесноком – это очень вкусно, сытно и быстро! Крабовые палочки и сметана обеспечивают нежный вкус, а чеснок придаёт пикантность и аппетитный аромат.', 3);
