INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Варим макароны', 'В небольшую кастрюлю наливаем воду, добавляем 1 щепотку соли, доводим до кипения. Опускаем макароны в кипящую воду, перемешиваем и варим, периодически помешивая, примерно 8-10 минут или согласно инструкции на упаковке, до мягкости.', 15), (DEFAULT, 2, 'Нарезаем продукты', 'Нарезаем крабовые палочки кружочками. Чеснок очищаем и нарезаем мелкими кусочками.', 15), (DEFAULT, 3, 'Готовим в сковороде', 'В сковороде растапливаем сливочное масло. Чеснок обжариваем на сливочном масле 1-2 минуты. Всыпаем муку, добавляем соль, перец и прованские травы. Быстро перемешиваем. Добавляем сметану, крабовые палочки, перемешиваем и готовим 2 минуты', 15), (DEFAULT, 4, 'Добавляем макароны', 'Добавляем отваренные макароны, перемешиваем и прогреваем 2 минуты.', 15), (DEFAULT, 5, 'Добавляем сыр', 'Сыр натираем на мелкой тёрке. Подаём блюдо в горячем виде, посыпав сверху натёртым сыром.', 15);
INSERT INTO recipe_food_values VALUES (DEFAULT, 40, 160, 400, 300, 15);
INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (16, 'Помидоры на сковороде с чесноком жареные', 15, 100, 5, 'https://static.1000.menu/res/640/img/content-v2/dd/01/40005/pomidory-na-skovorode-s-chesnokom-jarenye_1571774831_13_max.jpg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'Легкая и быстрая в приготовлении овощная закуска! Жареные помидоры с чесноком – очень простое в приготовлении и по составу, но в то же время вкусное блюдо, которое может выступить не только легким перекусом, но и отличным вариантом для завтрака или ужина, гарниром к мясу или дополнением к гарниру.', 4);
INSERT INTO recipe_medias VALUES (16, 'https://static.1000.menu/res/380/img/content-v2/dd/01/40005/pomidory-na-skovorode-s-chesnokom-jarenye_1571772067_1_max.jpg', 1), (16, 'https://static.1000.menu/res/380/img/content-v2/dd/01/40005/pomidory-na-skovorode-s-chesnokom-jarenye_1571772067_4_max.jpg', 1), (16, 'https://static.1000.menu/res/380/img/content-v2/dd/01/40005/pomidory-na-skovorode-s-chesnokom-jarenye_1571772067_6_max.jpg', 1), (16, 'https://static.1000.menu/res/640/img/content-v2/dd/01/40005/pomidory-na-skovorode-s-chesnokom-jarenye_1571774831_13_max.jpg', 1); 
