INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Помидоры моем, даем воде стечь или просушиваем бумажными полотенцами. Нарезаем томаты на кружочки толщиной около одного сантиметра. На сковороду наливаем растительное масло. Хорошо разогреваем его, выкладываем на разогретое масло кружочки помидоров.', 16), (DEFAULT, 2, 'Жарим помидоры', 'Убавляем огонь до среднего, жарим помидоры с одной стороны около двух минут, не накрывая сковороду крышкой, в противном случае помидоры пустят сок и получится томатная паста.', 16), (DEFAULT, 3, 'Режем и добавляем чеснок.', 'Добавляем приготовленный чеснок, соль и черный молотый перец по вкусу. Держим овощи на огне еще 3-4 минуты, на пару минут сковороду можно прикрыть крышкой, чтобы чеснок пустил сок и сделал масло более ароматным.', 16), (DEFAULT, 4, 'Завершаем', 'крышку на сковороде долго не держим, чтобы помидоры не начали «расползаться». Аккуратно смещаем на сковороде помидоры с чесноком, чтобы помидоры впитали в себя ароматное масло. Выключаем огонь.', 16);
INSERT INTO recipe_food_values VALUES (DEFAULT, 10, 80, 50, 120, 16);
INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (17, 'Кабачки в муке на сковороде', 30, 140, 4, 'https://static.1000.menu/res/640/img/content-v2/3e/56/35696/kabachki-v-muke-na-skovorode_1615649164_11_max.jpg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'На скорую руку, очень просто, очень вкусно, на каждый день! Кабачки в муке на сковороде можно готовить на перекус, на закуску и на гарнир. Они подходят для завтрака и для ужина, хорошо сочетаются с чесноком, сыром, помидорами, другими овощами и мясом. Проще простого!', 4);
