INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Кабачки вымойте, обсушите. Если кожица толстая и жесткая, срежьте ее. Кабачки нарежьте кружочками толщиной около 1 см. Кабачки посолите, перемешайте и оставьте на 10-15 минут. Выделившийся сок слейте.', 17), (DEFAULT, 2, 'Панируем в просеянной муке', 'Кабачки с двух сторон запанируйте в муке. Я просто добавляю нарезанные кружочки кабачков в тарелку с мукой и перемешиваю все руками. Но вы можете обваливать каждый кусочек по отдельности.', 17), (DEFAULT, 3, 'Жарим', 'В сковороде разогрейте растительное масло и обжарьте на нем кабачки с двух сторон до появления золотистой корочки.', 17);
