INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Подготовка', 'Измельчите лук. Редис вымойте, отрежьте кончики с двух сторон, разрежьте на 2 части и нарежьте полукружочками. Помидоры вымойте и нарежьте на четыре части. Огурцы вымойте и нарежьте тонкими кружочками.', 18), (DEFAULT, 2, 'Готовим салат', 'В салатнице перемешайте черемшу с помидорами, огурцами, редисом и луком. Все это – подсолите по вкусу. Для оформления подачи нам также потребуются отваренные вкрутую яйца.', 18);
