INSERT INTO recipes (id, title, cooktime, calories, rating, title_image_url, author_id, description, recipe_type) VALUES (19, 'Салат из помидоров с огурцом и луком', 15, 150, 5, 'https://static.1000.menu/res/640/img/content-v2/dc/1d/36406/salat-iz-pomidorov-s-ogurcom-i-lukom_1613988871_7_max.jpg', 'WPLfq0KRbMg7ziylPtR3d2Wditr1', 'На скорую руку, очень вкусный, свежий, летний, яркий! Салат из помидоров с огурцом и луком без майонеза освежит любой стол. Его можно подавать в дополнение к мясным, рыбным блюдам или сам по себе.', 4);
