INSERT INTO recipes_steps VALUES (DEFAULT, 1, 'Нарезаем овощи', 'Огурцы нарежьте ломтиками небольшого размера и переложите в миску для салата. Помидоры нарежьте ломтиками примерно такого же размера и формы, как огурцы. Добавьте измельченные помидоры в миску к огурцам.', 19), (DEFAULT, 2, 'Добавляем лук', 'Лук почистите и нарежьте тонкими полукольцами или четверть кольцами. Добавьте к измельченным овощам, разделив отдельные ломтики лука руками. Остается только заправить салат из помидоров с огурцом и луком растительным маслом, посолить по вкусу.', 19);
